<template>  <div class="language" @click="setLang">{{$t('common.language')}}</div></template><script>  export default {    name: "language",    methods:{      setLang(){        let lang = this.$getLocalJson('lang').LANGUAGE        if(lang=='en'){          this.$setLang('zh_CN')        }else {          this.$setLang('en')        }        localStorage.removeItem('gdId')        localStorage.removeItem('address')        window.location.reload()      }    }  }</script><style scoped lang="less">  @import "index";</style>