<template>  <ul>    <li v-for="i in icons">      <div>        <x-icon :name="i"></x-icon>        <p>{{i}}</p>      </div>    </li>  </ul></template><script>  import icons from '../../../../weishengming-h5/common/utils/icons'  export default {    name: "icons",    computed:{        icons(){        return icons      }    }  }</script><style scoped lang="less">  ul{    display: flex;    flex-wrap: wrap;    li{      background: #fff;      transition: all .5s;      width: 25%;      border:1px solid #cccccc;      display: flex;      box-sizing: border-box;      div{        margin: 1rem 0;        width: 100%;        display: inline;        text-align: center;      }      .x-icon{        font-size: 4rem;        display: inline-block;        margin-bottom: 1rem;      }      transform: scale(1);      &:hover{        color: #3eff42;      }    }  }</style>