<template>  <div id="home" class="content">    <div class="head-user">      <div class="username">        <span>{{$t('me.LogIn')}}／</span>        <span>{{$t('me.Regist')}}</span>      </div>      <div class="user">        <div class="user-info">          <x-icon class="icon" name="location"></x-icon>          {{$t('me.Address')}}        </div>        <div class="user-info">          <x-icon class="icon" name="like"></x-icon>          {{$t('me.Favourites')}}        </div>      </div>      <div  class="userimg">        <x-icon class="icon" name="browsing-history"></x-icon>      </div>    </div>    <div class="me-menu-list">      <div class="CustomerService" @click="showTels">        {{$t('me.CustomerService')}}        <x-icon class="icon" name="phone"></x-icon>      </div>      <div class='faqtitle' >        {{$t('me.faqtitle')}}        <x-icon class="icon" name="warn"></x-icon>      </div>      <div class="setting" >        {{$t('me.Settings')}}        <x-icon class="icon" name="setting"></x-icon>      </div>    </div>    <language />    <!--联系电话-->    <x-actionsheet            v-model="show"            :actions="actions"            cancel-text="取消"            @select="onSelect"            @cancel="onCancel" v-if="actions"            :cancel-text="$t('common.cancel')"    />    <x-tabbar v-model="active">      <x-tabbar-item icon="tableware" to="/">{{$t('tabBar.order')}}</x-tabbar-item>      <x-tabbar-item icon="order" to="/tracking">{{$t('tabBar.tracking')}}</x-tabbar-item>      <x-tabbar-item icon="discover" to="/discover">{{$t('tabBar.discover')}}</x-tabbar-item>      <x-tabbar-item icon="me" to="/me">{{$t('tabBar.me')}}</x-tabbar-item>    </x-tabbar>  </div></template><script>  import language from "../../components/home/language"  export default {    name: "index",    data(){      return{        active:3,        show:false,        actions:null      }    },    components:{        language    },    methods: {        showTels () {            let lang = this.$getLocalJson('lang');            this.show=true;            this.$req({                url:this.$api.getcity,                method:'get',                data:{                    // language:language                }            }).then(res=>{                let {cities} = res.data;                let arr = []                cities.forEach(item=>{                    arr.push({                        name:`${item.cityName}  ${item.csrPhone}`,                        value:item.csrPhone,                        className:'color'                    })                })                console.log(arr)                this.actions=arr;            },err=>{                console.log(err)            })        }    },    created(){    }  }</script><style scoped lang="less">  @import "index";</style>